mapper.insert:
INSERT INTO FIX_TP
(
F_SEQ
,F_NM
,F_KIND		
,F_UNIT
,F_YEAR
)
VALUES
(
TO_CHAR(SYSDATE,
'YYYY') || LPAD(F_NUM_SEQ.NEXTVAL, 3, '0')
, #{f_NM}
, #{f_KIND}
, #{f_UNIT}
, #{f_YEAR}
)

mapper.update:
UPDATE FIX_TP
   SET F_SEQ = #{f_SEQ}
	 , F_NM = #{f_NM}
	 , F_KIND = #{f_KIND}
	 , F_UNIT = #{f_UNIT}
	 , F_YEAR = #{f_YEAR}
 WHERE F_SEQ = #{f_SEQ}
